const sheetA = $items("Extract XLSX A");
const sheetB = $items("Extract XLSX B");

const fieldsToCompare = [
  "username","email","password","firstname","lastname",
  "profile_field_cpf","profile_field_telefone","CEP",
  "profile_field_mensalidades",
  ...Array.from({ length: 30 }, (_, i) => `profile_field_pagto${String(i + 1).padStart(2, '0')}`),
  "profile_field_ensinosuperior",
  "profile_field_analisepessoal",
  "profile_field_ta",
  "profile_field_estagio",
  "profile_field_preaula1",
  "profile_field_preaula2",
  "profile_field_preaula3",
  "profile_field_preaula4",
  "profile_field_preaula5",
  "profile_field_concluiuatemodulo",
  "profile_field_migracao"
];

const inconsistencies = [];
const maxLines = Math.max(sheetA.length, sheetB.length);

for (let i = 0; i < maxLines; i++) {
  const rowA = sheetA[i]?.json || {};
  const rowB = sheetB[i]?.json || {};

  for (const field of fieldsToCompare) {
    const valueA = (rowA[field] ?? "").toString().trim();
    const valueB = (rowB[field] ?? "").toString().trim();

    if (valueA !== valueB) {
      inconsistencies.push({
        linha: i + 2,
        campo: field,
        valor_planilha_a: valueA || "VAZIO",
        valor_planilha_b: valueB || "VAZIO"
      });
    }
  }
}

return inconsistencies.map(i => ({ json: i }));
